#include <stdio.h>
#include <string.h>
#include <time.h>
#include <stdlib.h>
#include "private.h"

int main(int argc, char const *argv[])
{
    clock_t start, finish;
    double  duration;
    start = clock();


    unsigned char input [256] = {0x22,0x21,0x12,0xed,0xf2};        // message to encrypt
    private_encrypt(input);

    char base64[] = "";
    private_decrypt(base64);

    
    finish = clock();
    duration = (double)(finish - start) / CLOCKS_PER_SEC;   // 打印加解密过程持续的时间
    printf( "%f seconds\n", duration );

    return 0;
}